/**
 * @param {number[]} nums
 * @param {number} target
 * @return {number[]}
 */
var twoSum = function(nums, target) {
    let cache={}
    for(let i=0;i<nums.length;i++){
        let curTar=target-nums[i]
        if(cache[nums[i]]!==undefined){
            // console.log(cache[nums[i]])
            return [cache[nums[i]],i]
        }
        cache[curTar]=i
        // console.log(cache)
    }
};
