/**
 * @param {number} num
 * @return {string}
 */
var intToRoman = function(num) {
    let n2r={
1       :          'I',      
5       :          'V',      
10      :          'X',      
50      :          'L',      
100     :          'C',      
500     :          'D',      
1000    :          'M',      
    }
    let str=num+''
    let res=''
    for(let i=str.length-1;i>=0;i--){
        let temp=''
        let cur=+str[i]
        let bit=Math.pow(10,str.length-i-1),val=bit*cur
        if(cur<4){
            temp=n2r[bit].repeat(cur)
        }else if(cur===4){
            temp=n2r[bit]+n2r[bit*5]
        }else if(cur<9){
            temp=n2r[bit*5]+n2r[bit].repeat(str[i]-5)
        }else if(cur===9){
            temp=n2r[bit]+n2r[bit*10]
        }
        res=temp+res
    }
    return res
};
