/**
 * @param {number[]} nums
 * @return {number}
 */
var longestConsecutive = function(nums) {
    let map={},max=0
    for(let i=0;i<nums.length;i++){
      let cur=nums[i]
      if(map[cur]!=null)continue
      let left=map[cur-1]?map[cur-1]:0
      let right=map[cur+1]?map[cur+1]:0
      let sum=left+right+1
      map[cur]=sum
      max=Math.max(max,sum)
      map[cur-left]=sum
      map[cur+right]=sum
    }
    return max
};
