/**
 * @param {string} s
 * @return {number}
 */
var romanToInt = function(s) {
    let cache={
        I:1,
        V:5,
        X:10,
        L:50,
        C:100,
        D:500,
        M:1000
    }
    let sum=0
    for(let i=0;i<s.length;i++){
        let res
        let cur=cache[s[i]]
        // let next=i<s.length-1?cache[s[i+1]]:null
        let next=cache[s[i+1]]
        if(next  && cur < next){
            res=next-cur
            i++
        }else{
            res=cur
        }
        sum+=res
    }
    return sum
};
