/**
 * @param {number[]} nums
 * @return {number}
 */
var findPeakElement = function(nums) {

    // for(let i=1;i<nums.length;i++){
    //   if(nums[i]<nums[i-1])return i-1
    // }
    // return nums.length-1

      var n=nums.length;
    var l=0, r=n-1;
    while(l<r) {
        var mid = Math.floor((l+r)/2);
        if(nums[mid]<nums[mid+1]) {
            l=mid+1;
        } else {
            r=mid;
        }
    }
    return l;
};
