/**
 * @param {character[][]} grid
 * @return {number}
 */
var numIslands = function(grid) {
    let count=0
    if(grid.length===0)return count
    let r=grid.length,c=grid[0].length
    for(let i=0;i<r;i++){
      for(let j=0;j<c;j++){
        if(grid[i][j]===true)continue
        if(grid[i][j]==="1"){
          count++
          dfs(grid,i,j)
        }
      }
    }

    function dfs(grid,i,j){
      if(grid[i][j]==="1"){
        grid[i][j]=true
        if(i>0)dfs(grid,i-1,j)
        if(j>0)dfs(grid,i,j-1)
        if(i<r-1)dfs(grid,i+1,j)
        if(j<c-1)dfs(grid,i,j+1)      
      }
    }
    return count
};
