var invertTree = function(root) {
    if(root===null){return null}
    let arr=[root]
    function BFS(arr){
        let tempNode
        while(arr.length>0){
            let temp=[]
            for(let i=0;i<arr.length;i++){
                tempNode=arr[i].left
                arr[i].left=arr[i].right
                arr[i].right=tempNode
                if(arr[i].left)temp.push(arr[i].left)
                if(arr[i].right)temp.push(arr[i].right)
            }
            arr=temp;
        }
    }
    BFS(arr)
    return root
}
