var findDuplicate = function(nums) {
  let l=1,
    hi=nums.length-1
  function bS(arr,l,hi){
    if(l>=hi)return l
    let mid=Math.floor((l+hi)/2)
    let count=0
    for(let i=0;i<nums.length;i++){
      if(nums[i]<=mid)count++
    }
    if(count<=mid)l=mid+1
    else hi=mid
    return bS(arr,l,hi)
  }
  return bS(nums,l,hi)
};
