/**
 * @param {string} secret
 * @param {string} guess
 * @return {string}
 */
var getHint = function(secret, guess) {
  let ANum=0,BNum=0,hash={}
  for(let i=0;i<secret.length;i++){
    if(secret[i]===guess[i])ANum++
    else{
      if(!hash[secret[i]])hash[secret[i]]=1
      else hash[secret[i]]++
    }
  }
  for(let i=0;i<secret.length;i++){
    if(secret[i]!==guess[i]){
      if(hash[guess[i]]>0){
        hash[guess[i]]--
        BNum++
      }
    }
  }
  console.log(hash)
  return ANum+"A"+BNum+"B"
};
