/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
var rob = function(root) {
    function getMax(node){
        if(!node)return 0     
        if(node.maxSum)return node.maxSum
        let nl=node.left,nr=node.right,nll=null,nlr=null,nrl=null,nrr=null
        if(nl){
            nll=nl.left
            nlr=nl.right
        }
        if(nr){
            nrl=nr.left
            nrr=nr.right        
        }
        let sumOdd=getMax(nll)+getMax(nlr)+getMax(nrl)+getMax(nrr)+node.val
        let sumEven=getMax(nl)+getMax(nr)
        let finalSum=Math.max(sumOdd,sumEven)
        node.maxSum=finalSum
        return finalSum
    }
    return getMax(root)
};
