/**
 * @param {number} num
 * @return {number[]}
 */
// 1位数 0,1
// 2位数 0,1,(0+1,1+1)
// 3位数 0,1,1,2,(0+1,1+1,1+1,2+1)
var countBits = function(num) {

    let arr=[0]

    let bitCount=1;


    for(let i=1;i<=num;i++){

        if(i>=bitCount*2){
            bitCount*=2;

        }
        arr[i]=arr[i-bitCount]+1


    }

    return arr
};
