/**
 * @param {string} s
 * @return {string}
 */
var reverseVowels = function(s) {
  let newS=''
  let vowels={
    a:1,A:1,e:1,E:1,i:1,I:1,o:1,O:1,u:1,U:1
  }
  let stack=[]
  for(let i=0;i<s.length;i++){
    if(vowels[s[i]]){
      stack.push(s[i])
    }
  }
  for(let i=0;i<s.length;i++){
    if(vowels[s[i]])newS+=stack.pop()
    else newS+=s[i]
  }
  return newS
};
