/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number[]}
 */
var intersection = function(nums1, nums2) {
    let arr1=[],res=[]
    for(let i=0;i<nums1.length;i++){
        arr1[nums1[i]]=1
    }
    for(let i=0;i<nums2.length;i++){
        if(arr1[nums2[i]])arr1[nums2[i]]=true
    }
    for(let i=0;i<arr1.length;i++){
        if(arr1[i]===true)res.push(i)
    }
    return res
};
