/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number[]}
 */
var intersect = function(nums1, nums2) {
    let insertion=[]
    let cache=[]
    for(let i=0;i<nums1.length;i++){
        let cur=nums1[i]
        if(cache[cur]==null)cache[cur]=1
        else cache[cur]++
    }

    for(let i=0;i<nums2.length;i++){
        let cur=nums2[i]
        if(cache[cur]>0){
            insertion.push(cur)
            cache[cur]--
        }
    }
    return insertion
};
