/**
 * @param {character[][]} board
 * @return {boolean}
 */
var isValidSudoku = function(board) {
  function checkCore(cur,uniq){
      if(cur==='.')return true
      if(uniq[cur]!=null)return false
      else uniq[cur]=cur
      return true
  }
  function checkRow(board,n){
    let uniq=[]
    for(let i=0;i<9;i++){
      if(!checkCore(board[n][i],uniq))return false
    }
    return true
  }
  function checkCol(board,n){
    let uniq=[]
    for(let i=0;i<9;i++){
      if(!checkCore(board[i][n],uniq))return false
    }
    return true
  }
  function checkBlock(board,n){
    let uniq=[]
    let rS=Math.floor(n/3)*3,rE=rS+2
    let cS=Math.floor(n%3)*3,cE=cS+2
    for(let i=rS;i<=rE;i++){
      for(let j=cS;j<=cE;j++){
        if(!checkCore(board[i][j],uniq))return false
      }
    }
    return true
  }
  for(let i=0;i<9;i++){
    if(!checkRow(board,i) ||
    !checkCol(board,i) ||
    !checkBlock(board,i))
      return false
  }
  return true
};
