/**
 * @param {number} num
 * @return {boolean}
 */
var isPerfectSquare = function(num) {
    let lo=1,
        hi=Number.MAX_SAFE_INTEGER
    while(lo<hi){
      let mid=Math.floor((lo+hi)/2)
      let cur=mid*mid
      if(cur<num)lo=mid+1
      else if(cur>num)hi=mid
      else return true     
    }
    return false
};
