/**
 * @param {number} n
 * @return {string}
 */
var countAndSay = function(n) {
  function next(str){
    let count=1,newS=''
    for(let i=0;i<str.length-1;i++){
      let _next=str[i+1],
          cur=str[i]
      if(cur===_next){
        count++
      }else{
        newS+=count+''+cur
        count=1
      }
    }
    return newS+count+str[str.length-1]
  }
  let nextStr='1',idx=1
  while(idx++<n){
    nextStr=next(nextStr)
  }
  return nextStr
};
