 * @return {number}
 */
var lengthLongestPath = function(input) {
    // let paths=[]
    let tTime=0
    let curPath=''
    let isFile=false
    let maxCount=0
    let tempCount=0
    let tempList=[]
    for(let i=0;i<input.length;i++){
        if(input[i]==="."){
            isFile=true
        }
        if(input[i]==="\n"){


            if(tTime<tempList.length ){
                let t=tempList.length-tTime
                for(let i=0;i<t;i++){

                    tempCount-=tempList.pop()
                }
            }

            tempCount+=curPath.length
            tempList[tTime]=curPath.length

            if(isFile){
                maxCount=Math.max(maxCount,tempCount+tempList.length-1)
            }
            // console.log(curPath,tempList,maxCount)
            // paths.push([curPath,curPath.length,tTime,isFile])
            tTime=0
            curPath=''
            isFile=false
            while(input[++i]==='\t'){
                tTime++   
            }
        }
        curPath+=input[i]
    }
    // paths.push([curPath,curPath.length,tTime,isFile])
    // console.log(paths)
    if(tTime<tempList.length ){
        let t=tempList.length-tTime
        for(let i=0;i<t;i++){

            tempCount-=tempList.pop()
        }
    }
    tempCount+=curPath.length
    tempList[tTime]=curPath.length
    if(isFile){
        maxCount=Math.max(maxCount,tempCount+tempList.length-1)
    }
    return maxCount
};
