/**
 * @param {number} num
 * @return {string[]}
 */
var readBinaryWatch = function(num) {
    let arr=[1,2,4,8,1,2,4,8,16,32]
    let len=arr.length;
    let result=[];
    let hour=0,minute=0; 
    function recursiveCalc(index,recurNum){
        if(index>=0){ 
            if(index<4){
                if(hour+arr[index]>11){
                    return
                }
                recurNum--;
                hour+=arr[index]
            }else{
                if(minute+arr[index]>59){
                    return
                }
                recurNum--;
                minute+=arr[index]
            }
        }
        if(recurNum<=0){
            result.push(hour+(minute<10?(":0"+minute):(":"+minute)))
            backTrack(index)
            return;
        }
        for(let i=index+1;i<len;i++){
            recursiveCalc(i,recurNum)
        }
        backTrack(index)
    }

    function backTrack(index){
        if(index<4){
            hour-=arr[index]
        }else{
            minute-=arr[index]
        }
    }
    recursiveCalc(-1,num)
    return result

};
