/**
 * @param {number} n
 * @return {string[]}
 */
var fizzBuzz = function(n) {
    let res=[]
    for(let i=0;i<n;i++){
        if((i+1)%15===0){
            res[i]="FizzBuzz"
        }else if((i+1)%5===0){
            res[i]="Buzz"
        }else if((i+1)%3===0){
            res[i]="Fizz"
        }else{
            res[i]=''+(i+1)
        }
    }
    return res
};
