/**
 * @param {string} s
 * @return {string}
 */
var frequencySort = function(s) {
    let cache={}
    let str=''
    for(let i=0;i<s.length;i++){
        if(!cache[s[i]]){
            cache[s[i]]=1
        }else{
            cache[s[i]]++;
        }
    }

    let arr=Object.keys(cache).sort((a,b)=>cache[b]-cache[a])
    // console.log(arr)
    for(let i=0;i<arr.length;i++){
        str+=arr[i].repeat(cache[arr[i]])
    }
    return str

};
