/**
 * @param {number[][]} matrix
 * @return {number[]}
 */
var findDiagonalOrder = function(matrix) {
    if(matrix.length===0)return []
    let colL=matrix.length, rowL=matrix[0].length, max=colL*rowL
    let result=[]
    toRightTop(result,0,0)
    function toRightTop(result,i,j){
        if(result.length>=max)return
        if(i<0 || j>=rowL){
            if(j>=rowL){ j=rowL-1; i+=2}
            else if(i<0) i=0
            toLeftDown(result,i,j)        
        }else{
            result.push(matrix[i][j])
            toRightTop(result,i-1,j+1)      
        }
    }
    function toLeftDown(result,i,j){
        if(result.length===max)return
        if(i>=colL || j<0){
            if(i>=colL){ i=colL-1; j+=2}
            else if(j<0) j=0
            toRightTop(result,i,j)
        }else{
            result.push(matrix[i][j])
            toLeftDown(result,i+1,j-1)            
        }
    }
    return result
};
