/**
 * @param {number} num
 * @return {string}
 */
var convertToBase7 = function(num) {
    // return num.toString(7)
    if(num===0)return "0"
    let n=Math.abs(num),mod,symb,str=''
    symb=num<n?"-":""
    while(n>0){
        mod=n%7
        n=Math.floor(n/7)
        str=mod+str
    }
    return symb+str
};
