/**
 * @param {string} word
 * @return {boolean}
 */
var detectCapitalUse = function(word) {
    let lowerA='a'.charCodeAt()
    let cache={}
    function isLow(le){
        if(cache[le])return cache[le]
        cache[le]=(le.charCodeAt()-lowerA)>=0
        return cache[le]
    }
    function traUp(n){
        for(let i=n;i<word.length;i++){
            if(!isLow(word[i]))return false
        }
        return true
    }
    function traLow(n){
        for(let i=n;i<word.length;i++){
            if(isLow(word[i]))return false
        }
        return true
    }
    if(word.length===1)return true
    if(isLow(word[0])){
        return traUp(1)
    }else{
        if(isLow(word[1])){
            return traUp(2)
        }else{
            return traLow(2)
        }
    }
};
