 * @param {string} a
 * @param {string} b
 * @return {number}
 */
var findLUSlength = function(a, b) {
    let aLen=a.length;
    let bLen=b.length;
    if(aLen===0 && bLen===0 || a===b){
        return -1
    }else if(aLen>bLen){
        return aLen
    }else{
        return bLen
    }
};
