/**
 * @param {number} N
 * @return {number}
 */
var countArrangement = function(N) {
  let boolArr=Array(N).fill(false)
  let count=0
  function rev(all,cur,boolArr){
    if(cur<=0){
      count++;
      return
    }
    for(let i=1;i<=all;i++){
      if(boolArr[i-1] || (i % cur) !==0 && cur % i !==0){
        continue;
      }
      boolArr[i-1]=true
      rev(all,cur-1,boolArr)
      boolArr[i-1]=false
    }
  }

  rev(N,N,boolArr)
  return count;
};
