/**
 * @param {number[]} w
 */
var Solution = function(w) {
  this.sum=0
  this.p=[]
  for(let i=0;i<w.length;i++){
    this.sum+=w[i]
    this.p[i]=this.sum
  }
  // console.log(this.p)
};

/**
 * @return {number}
 */
Solution.prototype.pickIndex = function() {
    let ran=Math.random()*this.sum
    let lo=0,hi=this.p.length-1
    while(lo<hi){
      let mid=Math.floor((lo+hi)/2)
      if(this.p[mid]>ran)hi=mid
      else lo=mid+1
    }
    return hi
};

