/**
 * @param {string} a
 * @param {string} b
 * @return {string}
 */
var complexNumberMultiply = function(a, b) {
    let realA=parseInt(a)
    let imageA=parseInt(a.match(/-?[0-9]+i/)[0])
    let realB=parseInt(b)
    let imageB=parseInt(b.match(/-?[0-9]+i/)[0])

    let realSum=(realA*realB)+(imageA*imageB)*-1
    let imageSum=realA*imageB+imageA*realB
    return ''+realSum+"+"+imageSum+"i"
};
