/**
 * @param {number[][]} wall
 * @return {number}
 */
var leastBricks = function(wall) {
    let c=[],max=-1,eachW=wall.length
    for(let i=0;i<wall.length;i++){
        let last=0
        for(let j=0;j<wall[i].length-1;j++){
            let cur=wall[i][j]+last
            last=cur
            if(!c[cur])c[cur]=1
            else c[cur]++
            if(c[cur]>max)max=c[cur]
        }
    }
    if(max<0)return eachW
    else return eachW-max
};
