/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
var findTilt = function(root) {
    let sum=0
    function getSum(node){       
        if(!node)return 0
        return node.sum=getSum(node.left)+getSum(node.right)+node.val
    }
    function getTilt(node){
        if(!node)return
        if(node.left)getTilt(node.left)
        if(node.right)getTilt(node.right)
        sum+=Math.abs((node.left?node.left.sum:0)-(node.right?node.right.sum:0))
    }
    getSum(root)
    getTilt(root)
    return sum
};
