/**
 * @param {number[]} nums
 * @return {number}
 */
var arrayNesting = function(nums) {
    let cache={}
    let max=0
    for(let i=0;i<nums.length;i++){
        let cur=nums[i]
        let count=0
        while(cache[cur]===undefined){
            cache[cur]=1
            count++
            cur=nums[cur]
        }
        if(count>max)max=count
    }
    return max
};
