var minDistance = function(a, b) {
  let aL=a.length,bL=b.length;
  let arr=[]
  for(let i=0;i<=aL;i++){
    arr[i]=[]
    for(let j=0;j<=bL;j++){
      arr[i][j]=0
    }
  }
  for(let i=1;i<=aL;i++){
    for(let j=1;j<=bL;j++){
      if(a[i-1]===b[j-1]){
        arr[i][j]=arr[i-1][j-1]+1
      }else{
        arr[i][j]=Math.max(arr[i-1][j],arr[i][j-1])
      }
    }
  }

  return aL+bL-2*arr[aL][bL]
};
