/**
 * @param {number} n
 * @return {number[][]}
 */
var generateMatrix = function(n) {
    let start=0,end=n-1,num=1
    let result=[]
    for(let i=0;i<n;i++){
        result[i]=[]
    }
    while(end>=start){
        let s=start  
        while(s<=end)result[start][s++]=num++
        s=start+1
        while(s<=end)result[s++][end]=num++
        s=end-1
        while(s>=start)result[end][s--]=num++
        s=end-1
        while(s>=start+1)result[s--][start]=num++
        start++;end--        
    }
    return result

};
