/**
 * @param {number} m
 * @param {number} n
 * @param {number[][]} ops
 * @return {number}
 */
var maxCount = function(m, n, ops) {
    let minR=m,minC=n
    for(let i=0;i<ops.length;i++){
        let cur=ops[i]
        if(cur[0]<minR)minR=cur[0]
        if(cur[1]<minC)minC=cur[1]
    }
    return minR*minC
};
