/**
 * @param {number[]} nums
 * @return {number}
 */
var triangleNumber = function(nums) {
    let sum=0
    nums.sort((a,b)=>a-b)
    for(let i=nums.length-1;i>=2;i--){
        let l=0,r=i-1
        while(l<r){
            if(nums[l]+nums[r]>nums[i]){
                sum+=r-l
                r--
            }else
                l++
        }
    }
    return sum
};
