/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} t1
 * @param {TreeNode} t2
 * @return {TreeNode}
 */
var mergeTrees = function(t1, t2) {
    function check(t1,t2){
        if(t1 && t1.val!==null && t2 && t2.val!==null){
            t2.val=t1.val+t2.val
        }else if(t1 && t1.val!==null){
            if(!t2){t2=new TreeNode()};
            t2.val=t1.val
        }
        return t2
    }
    function DFS(t1,t2){
        t2=check(t1,t2)
        if(t1 && t1.val!==null){
            t2.left=DFS(t1.left,t2 && t2.left)
            t2.right=DFS(t1.right,t2 && t2.right)
        }
        return t2
    }
    return DFS(t1,t2)
};
