/**
 * @param {number} m
 * @param {number} n
 * @return {number}
 */
var uniquePaths = function(m, n) {
    let aux=[]
    for(let i=0;i<m;i++){
        aux[i]=[]
        for(let j=0;j<n;j++){
            aux[i][j]=0
        }
    }
    aux[0][0]=1

    for(let i=0;i<m;i++){
        for(let j=0;j<n;j++){
            let o=aux[i][j]
            if(i<m-1)aux[i+1][j]+=o
            if(j<n-1)aux[i][j+1]+=o
        }
    }

    return aux[m-1][n-1]
};
