/**
 * @param {character[]} tasks
 * @param {number} n
 * @return {number}
 */
var leastInterval = function(tasks, n) {
    let c=Array(26).fill(0)
    let codeA='A'.charCodeAt(0)
    let map={},excess=0,need=0,result=0
    for(let i=0;i<tasks.length;i++){  
        c[tasks[i].charCodeAt(0)-codeA]++
    }
    let last=0,temp
    while(true){
        temp=0
        for(let i=0;i<c.length;i++){
            if(c[i]<=0)continue
            if(c[i]>0){temp++;c[i]--}
        }
        if(temp===0){
            if(n+1>last){
                result=result-n-1+last
                need=need-n-1+last
            }
            break;
        }
        last=temp
        if(temp>n+1)excess+=temp-n-1
        else need+=n+1-temp
        result+=Math.max(temp,n+1)
    }
    if(excess<need)return result-excess
    else return result-need
};
