/**
 * @param {number[]} nums
 * @return {number}
 */
var maximumProduct = function(nums) {
    let max1,max2,max3,min1,min2
    max1=max2=max3=-Infinity
    min1=min2=Infinity

    for(let i=0;i<nums.length;i++){
        let cur=nums[i]
        if(cur>max1){
            max3=max2
            max2=max1
            max1=cur
        }else if(cur>max2){
            max3=max2
            max2=cur
        }else if(cur>max3){
            max3=cur
        }

        if(cur<min1){
            min2=min1
            min1=cur
        }else if(cur<min2){
            min2=cur
        }
    }
    return Math.max(max1*max2*max3,min1*min2*max1)
};
