/**
 * @param {number} n
 * @param {string[]} logs
 * @return {number[]}
 */
var exclusiveTime = function(n, logs) {
  let first=logs[0].split(":")
  let cache=Array(n).fill(0),stack=[first[0]],lastTime=first[2],lastStatus=first[1]
  for(let i=1;i<logs.length;i++){
    let cur=logs[i].split(":")
    let id=cur[0],
      status=cur[1],
      time=cur[2]
    // if(lastStatus){
      let lastId=stack[stack.length-1]
      if(status==="start"){
        cache[lastId]+=time-lastTime-(lastStatus==="start"?0:1)
        stack.push(id)
      }
      else if(status==="end"){
        cache[id]+=time-lastTime+(lastStatus==="start"?1:0)
        stack.pop()
      }
    // }else stack.push(id)
    lastTime=time
    lastStatus=status
  }
  return cache
};
