var findErrorNums = function(nums) {
  let mem=Array(nums.length).fill(false),err,right
  for(let i=0;i<nums.length;i++){
    if(mem[nums[i]-1])err=nums[i]
    mem[nums[i]-1]=true
  }
  for(let i=0;i<mem.length;i++){
    if(!mem[i])return [err,i+1]
  }
