var minSteps = function(n) {
    // let steps={
    //     1:0,
    // }
    let sum=0
    function iter(n){
        if(n===1)return
        let i=2
        while(n%i!==0){
            i++
        }
        sum+=i
        iter(n/i)
    }

    iter(n)
    return sum
};
