/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} root
 * @return {string[][]}
 */
var printTree = function(root) {

    function getHeight(node){
        if(!node)return 0
        return Math.max(getHeight(node.left),getHeight(node.right))+1
    }
    function bfsResult(arr,col,row,resultArr,depth){
        while(depth>0){
            let temp=[]
            if(col)col=Math.floor(col/2)
            for(let i=0;i<arr.length;i++){
                if(arr[i])resultArr[row-depth][col+(i*Math.pow(2,depth))]=arr[i].val+''
                temp.push(arr[i]?arr[i].left:null)
                temp.push(arr[i]?arr[i].right:null)
            }
            depth--
            arr=temp            
        }
    }    
    let col=0,row=getHeight(root),resultArr=[]
    col=Math.pow(2,row)-1
    for(let i=0;i<row;i++){
        resultArr[i]=[]
        for(let j=0;j<col;j++){
            resultArr[i][j]=''
        }
    }
    bfsResult([root],col,row,resultArr,row)
    return resultArr

};
