/**
 * @param {number[]} nums
 * @return {boolean}
 */
var isPossible = function(nums) {
  let last=nums[0]
  let splitArr=[1]
  let valueArr=[last]
  let curIdx=0
  let startIdx=0
  for(let i=1;i<nums.length;i++){
    let cur=nums[i]
    if(cur-last===1){
      splitArr[curIdx]++
      valueArr[curIdx]=cur
      last=cur
    }else if(cur===last){
      let prevIdx=null
      for(let i=splitArr.length-2;i>=startIdx;i--){
        if(valueArr[i]===cur-1 ){
          prevIdx=i
          break
        }
      }
      if(prevIdx!=null){
        splitArr[prevIdx]++
        valueArr[prevIdx]=cur 
        continue
      }
      curIdx++
      splitArr[curIdx]=1
      valueArr[curIdx]=cur
    }else{
      for(let i=startIdx;i<splitArr.length;i++){
        if(valueArr[i]===last){
          if( splitArr[i]<3)return false
          else{
            startIdx=i+1
            break
          }
        }
      }      
    }
  }


  for(let i=0;i<splitArr.length;i++){
    if(splitArr[i]<3){
      return false
    }
  }
  return true
};
