/**
 * @param {number} n
 * @param {number} k
 * @return {number[]}
 */
var constructArray = function(n, k) {
    let arr=[]
    if(k!==1){
        for(let i=0;i<=k;i++){
            let cur
            let aux=Math.floor(i/2)
            if(i%2===0){
                cur=k-aux
            }else{
                cur=aux
            }
            arr.push(n-cur)
        }
    }
    for(let i=n-arr.length;i>=1;i--){
        arr.push(i)
    }
    return arr
};
