/**
 * @param {number} m
 * @param {number} n
 * @param {number} k
 * @return {number}
 */
var findKthNumber = function(m, n, k) {

  let lo=1,hi=m*n

  while(lo<hi){
    let mid=Math.floor((lo+hi)/2)
    let count=0
    for(let i=1;i<=m;i++){
      // for(let j=1;j<=n;j++){
      //   if(i*j<=mid)count++
      // }
      count+=Math.min(n,Math.floor(mid/i))
    }
    if(count>=k)hi=mid
    else lo=mid+1
  }
  return lo

};
