/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
var findSecondMinimumValue = function(root) {
    let min=root.val,secMin=Infinity
    let arr=[root]
    let aux
    while(arr.length>0){
        aux=[]
        for(let i=0;i<arr.length;i++){
            if(arr[i].val>min)secMin=Math.min(arr[i].val,secMin)
            // if(arr[i].left && arr[i].left.val>min)
            // else 
            if(arr[i].left)aux.push(arr[i].left)
            if(arr[i].right)aux.push(arr[i].right)
        }
        arr=aux
    }
    return secMin===Infinity?-1:secMin
};
