var reverse = function(x) {
    if(x===0)return 0
    let sym=x<0?-1:1,lo=-Math.pow(2,31),hi=Math.pow(2,31)-1
    // let str=x+''
    // if(str.length>32 && str[str.length-1]!=="0")return 0
    // let rev=str.split('').reverse().join('')
    // // console.log(str)
    // let result= parseInt(rev,10)*sym
    // if(result<lo || result > hi)return 0
    // return result

    let strTest=x+''
    if(strTest.length>32 && strTest[strTest.length-1]!=="0")return 0
    let str=''
    x=Math.abs(x)
    while(x>0){
        str+=x%10
        x=Math.floor(x/10)
    }
    let result= parseInt(str,10)*sym
    if(result<lo || result > hi)return 0
    return result
};
