/**
 * @param {number} n
 * @return {number}
 */
var climbStairs = function(n) {
    let mem=[]
    function getSteps(n){
        if(mem[n])return mem[n]
        if(n===1)return 1
        if(n===2)return 2
        let steps=getSteps(n-1)+getSteps(n-2)
        mem[n]=steps
        return steps
    }
    return getSteps(n)
};
