  function WeightquickUnion(N){
    const sz=[]
    const id=[]
    // count为当前有多少个连通的组合
    let count=N
    // N为连接点的数量
    for(let i=0;i<N;i++){
      id[i]=i
      sz[i]=1
    }
    // 找到根节点
    this.find=function(i){
      while(id[i]!==i){
        i=id[i]
      }
      return i
    }
    this.count=function(){
      return count
    }
    // 判断两点是否已经连通
    this.connected=function (p,q) {
      return this.find(p)===this.find(q)
    }
    this.union=function (p,q) {
      let i=this.find(p)
      let j=this.find(q)
      // 相同的源头，不进行处理
      if(i===j)return
      // 判断大小树，小树连接到大树
      if(sz[i]<sz[j]){
        sz[j]+=sz[i]
        id[i]=j
      }else{
        sz[i]+=sz[j]
        id[j]=i
      }
      count--
    }
    this.showId=function(){
      return id
    }
    this.showSz=function () {
      return sz
    }
  }

